// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

interface IGB721Contract {
  function setTokenURI(uint256 tokenId, string memory tokenURI_) external ; 
  function setMarketplaceAddress(address _marketplaceAddress) external ;
  function mintToken(
    address account,
    uint256 tokenId,
    uint96 royalty,
    string memory tokenURI_
  ) external ;
  function royaltyInfo(uint256 _tokenId, uint256 _salePrice) external view returns (address, uint256);
  function ownerOf(uint256 tokenId) external view returns (address);
}

contracts/library/Domain.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;
import {
  OrderItem, 
  Order
} from "./GBStructs.sol";

library Domain {

  bytes32 constant public ORDER_ITEM_TYPEHASH = 
    keccak256(
      "OrderItem(address nftContract,address seller,bool isMinted,uint256 tokenId,string tokenURI, uint256 quantity,uint256 itemPrice,address charityAddress,uint96 charityShare,uint96 royaltyFee,uint256 deadline,uint256 salt)"
    );

  function _hashOrderItem(OrderItem memory item)
    internal 
    pure returns (bytes32)
  {
    return keccak256(
      abi.encode(
        ORDER_ITEM_TYPEHASH,
        item.nftContract,
        item.seller,
        item.isMinted,
        item.tokenId,
        keccak256(bytes(item.tokenURI)),
        item.quantity,
        item.itemAmount,
        item.charityAddress,
        item.charityShare,
        item.royaltyFee,
        item.deadline,
        item.salt
      )
    );
  }

}

